mapper.select:
SELECT plate, month, mile, createtime, operatorid, period, averagespeed, begmile, endmile FROM bizmileage
order by month
limit #{offset}, #{limit}

sql.where:
1 = 1

	and month >= #{monthStart}


	and #{monthEnd} >= month


	and plate = #{plate}


	and plate like CONCAT ('%', #{plateLike}, '%')
